function Animal() {}
Animal.prototype.sound = 'Roar';
var lion = new Animal();
console.log(lion.sound); // Outputs: Roar

var animal = { sound: 'Roar' };
var lion = Object.create(animal);
console.log(lion.sound); // Outputs: Roar

function Employee(name) {
  this.name = name;
}
Employee.prototype.getName = function() {
  return this.name;
};

function Manager(name, department) {
  Employee.call(this, name);
  this.department = department;
}

Manager.prototype = Object.create(Employee.prototype);
Manager.prototype.getDepartment = function() {
  return this.department;
};

var mgr = new Manager('Alice', 'HR');
console.log(mgr.getName()); // Outputs: Alice
console.log(mgr.getDepartment()); // Outputs: HR

function Person(firstName, lastName) {
    this.firstName = firstName;
    this.lastName = lastName;
}

Person.prototype.greeting = function() {
    return `Hello there, ${this.firstName} ${this.lastName}`;
};

function Customer(firstName, lastName, phone, membership) {
    Person.call(this, firstName, lastName);
    this.phone = phone;
    this.membership = membership;
}

Customer.prototype = Object.create(Person.prototype);
Customer.prototype.constructor = Customer;

const customer1 = new Customer('Tom', 'Smith', '123-456-7890', 'Standard');
console.log(customer1.greeting()); // Hello there, Tom Smith

Customer.prototype.greeting = function() {
    return `Hello there, ${this.firstName} ${this.lastName}, welcome to our company.`;
};

console.log(customer1.greeting()); // Hello there, Tom Smith, welcome to our company.
